import React, { useState } from "react";
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";

const AddTask = () => {
  const [newTask, setNewTask] = useState("Enter New Task");
  const [newDate, setNewDate] = useState(new Date())

  function addNewTask() {
    const taskAndDate ={
      task: newTask,
      date: newDate
    }
    fetch("http://localhost:8080/tasks", {
      method: "POST",
      mode: "cors",
      body: JSON.stringify(taskAndDate),
      headers: {
        "Content-Type": "application/json; charset=utf-8",
      },
    })
    .then(setNewDate(new Date()))
    .then(setNewTask("Enter New Task"))
  }
  return (
    <form className="add-form">
      <label>
        New Task:
        <input
          type="text"
          value={newTask}
          onChange={(event) => setNewTask(event.target.value)}
        />
      </label>
      <label>
        Due Date: 
      </label>
      <DatePicker selected={new Date(newDate)} onChange={(selectedDate) => setNewDate(selectedDate)}/>
      <input type='submit' id='submit-button' value={'Add Task'} onClick={(event) => {
        event.preventDefault()
        addNewTask();
        }}/>
    </form>
  );
};

export default AddTask;
