import React, { Component } from "react";
import Table from "./Table";
import AddTask from './AddTask';

export default class DillyDally extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tasks: [],
    };
  }

  componentDidMount() {
    fetch("http://localhost:8080/tasks", {
      method: "GET",
    })
      .then((response) => response.json())
      .then((data) => {
        this.setState({
          tasks: data,
        });
      })
      .catch((err) => console.log(err));
  }
  addToTable = (task) => {
    this.setState({
      tasks: [...task]
    })
  }

  removeFromTable = (task) => {
    let index = this.tasks.indexOf(task);
    let newTasks = this.tasks.splice(index, 1)
    this.setState({
      tasks: newTasks
    })
  }

  render() {
    return(
      <>
        <nav>Dilly Dally</nav>
        <AddTask />
        <Table tasks={this.state.tasks} add={this.addToTable} remove={this.removeFromTable}/>
      </>
    )
  }
}
